import React, {Component} from 'react';
import {Container, Button} from'reactstrap'
import {render}from 'react-dom';
import{Link, useParams} from'react-router-dom';
import axios from'axios';
import { FontAwesomeIcon }from '@fortawesome/react-fontawesome'
import {faChevronCircleRight, faFeatherAlt}from '@fortawesome/free-solid-svg-icons'

        const{REACT_APP_HOST} = process.env;

class Evaluacion extends Component {

    constructor() {
        super();
        this.state = {
            preguntas_respuestas: []
        }
    }

    componentDidMount() {
        this.getPreguntasRespuestas();

    }

    getPreguntasRespuestas() {
        const {match} = this.props;
        const id_taller = match.params.id;
        axios.get(`${REACT_APP_HOST}/api/respuestas/` + id_taller).then((response) => {
            this.setState({preguntas_respuestas: response.data});
            this.mostarEvaluacion();
        });
    }

    mostarEvaluacion() {
        const contenedor_eval = document.getElementById('contenedor_eval');
        const preguntasYrespuestas = [];
        this.state.preguntas_respuestas.map((preguntaAct, numPregunta) => {
            const respuestas = [];
            preguntaAct.respuestas.map((respuestaAct, numRespuesta) => {
                respuestas.push(
                        `<label><input type="radio" name="${numPregunta}" value="${respuestaAct.estadoresp}"/> ${respuestaAct.respuesta}</label><br/> `
                        );
            });

            preguntasYrespuestas.push(
                    `
                        <div class="font-weight-bold">${numPregunta + 1 + ':'} ${preguntaAct.pregunta}</div>
                        <div class="respuestas pl-4 pt-2">${respuestas.join('')}</div><hr/>
                    `
                    );

        });
        contenedor_eval.innerHTML = preguntasYrespuestas.join('');

    }

    validarRespuestas() {
        const respuestas = document.querySelectorAll('.respuestas');
//        const respuestas = document.getElementsByName('1');
        let cont_correctas = 0;       
         this.state.preguntas_respuestas.map((respuestaAct, indexPreg)=>{
            const resp_x_preguntas=respuestas[indexPreg];   

            const checkSelect=`input[name='${indexPreg}']:checked`;
            const resp_elegida=(resp_x_preguntas.querySelector(checkSelect)).value;
            alert(resp_elegida);
        });
//        
    }

    render() {
        return(
                <Container>
                    <div className='container col-md-10 bg-primary text-white'>
                        <h2 className="p-2"><FontAwesomeIcon icon={faFeatherAlt}/> Evaluacion</h2>
                    </div>
                    <div className='container col-md-10 border p-4' id='contenedor_eval'>

                    </div>
                    <div className='container bg-primary clearfix  col-md-10 p-2 '>
                        <div className='bg-danger float-right'>
                            <Button onClick={()=>{this.validarRespuestas();}} size='sm' color='success'><FontAwesomeIcon icon={faChevronCircleRight}/> Calificar</Button>
                        </div>
                    </div>
                </Container>
                );
    }
}

export default Evaluacion;
