import React, {Component}from 'react';
import{Link} from'react-router-dom';
import {render}from'react-dom';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faHeadset, faUser} from '@fortawesome/free-solid-svg-icons';
import Cookies from 'universal-cookie';
import {Modal, Button,  Dropdown, DropdownToggle, DropdownMenu, DropdownItem}from'reactstrap';


const cookies = new Cookies();
const {REACT_APP_HOST}=process.env;

export default class Navigation extends Component {

    constructor() {
        super();
        this.state = {
            modalopen: false,
            dropdownOpen:false
        }
    }

    modalShow() {
        this.setState({modalopen: true})
    }
    hideModal() {
        this.setState({modalopen: false})
    }
    dropdownOpen(){
        this.setState({dropdownOpen:true})
    }
    toggle(){

    }

    componentDidMount() {
        if (!cookies.get('nombre')) {
            window.location.href = '/'
        }

    }
    cerrar_session() {
        cookies.remover('id', {path: '/'});
        cookies.remover('nombre', {path: '/'});
        cookies.remover('apellido', {path: '/'});
        cookies.remover('user', {path: '/'});
        cookies.remover('rol', {path: '/'});
        cookies.remover('cedula', {path: '/'});
        cookies.remover('image', {path: '/'});
        window.location.href = '/';
    }

    render() {
        return(
                <div>
                    <nav className="navbar navbar-expand-lg navbar-light bg-dark">
                        <Link className="navbar-brand text-white" to="/home">TAREAS DEL DIA</Link>
                        <button className="navbar-toggler bg-white" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className=" collapse navbar-collapse" id="navbarNav">
                            <ul className=" navbar-nav">
                                <li className="nav-item active">
                                    <Link className="nav-link text-white" to="/home">Home</Link>
                                </li>
                                <li className="nav-item">
                                    <Link className="nav-link text-white"  to="/adminTask">Talleres</Link>
                                </li>
                                <li className="nav-item">
                                    <Link className="nav-link text-white"  to="/adminAreas">Areas</Link>
                                </li>
                                <li className="nav-item">
                                    <Link className="nav-link text-white" to="/adminUsers">Users</Link>
                                </li>

                            </ul>
                            <ul className="navbar-nav ml-auto">
                                <FontAwesomeIcon icon={faHeadset} style={{fontSize: 30}}/>
                                <a className="mano nav-link support text-white mr-4" onClick={() => {
                                        this.modalShow()
                                       }}>Soporte</a> 
                                {cookies.get('image')?
                                <img width='35' height='38' src={`${REACT_APP_HOST}/public_image_user/`+cookies.get('image')} alt='Image'/>
                                :
                                <FontAwesomeIcon icon={faUser}  style={{fontSize: 30}}/>
                                }
                                <li className="nav-item dropdown ">
                                    <a className="nav-link dropdown-toggle text-white" id="navbarDropdown" role="button" data-toggle="dropdown" aria-expanded="true" aria-haspopup="true"><span className="text-white user-namefont">{cookies.get('nombre')} </span><span className="text-white user-namefont">{cookies.get('apellido')}</span></a>
                                    <div className="dropdown-menu navbar-light bg-dark " aria-labelledby="navbarDropdown" id="subMenu">

                                        <a id="idAprobados2" className=" dropdown-item " href="#">Resultados</a>                               
                                        <a className=" dropdown-item" href="#">Usuarios</a>

                                        <div className="dropdown-divider"></div>
                                        <a className=" dropdown-item" href="#">LogOut</a> 
                                    </div>

                                </li>
                                <Dropdown isOpen={()=>{this.dropdownOpen()}} toggle={()=>{this.toggle()}}>
      <DropdownToggle caret>
       {cookies.get('nombre')}{cookies.get('apellido')} 
      </DropdownToggle>
      <DropdownMenu>
        <DropdownItem header>Header</DropdownItem>
        <DropdownItem>Some Action</DropdownItem>
        <DropdownItem text>Dropdown Item Text</DropdownItem>
        <DropdownItem disabled>Action (disabled)</DropdownItem>
        <DropdownItem divider />
        <DropdownItem>Foo Action</DropdownItem>
        <DropdownItem>Bar Action</DropdownItem>
        <DropdownItem>Quo Action</DropdownItem>
      </DropdownMenu>
    </Dropdown>


                            </ul>
                        </div>
                    </nav>
                    <Modal isOpen={this.state.modalopen}>                      
                            <div className='container row p-2'>
                                <div className='col-md-11' style={{padding: 5}}>
                                    <h5 className='pl-3'>Soporte</h5>
                                </div>
                                <div className='col-md-1' style={{padding: 5}}>
                                    <Button size='sm' onClick={() => {
                                            this.hideModal()
                                                }}>X</Button>   
                                </div><hr/>
                            </div>                          

                        <div>
                            <div class="modal-body border">
                                <div class="container border pt-2">
                                    <p>
                                        <label>Telefono: 0992094788</label><br/>
                                        <label>Correo: pcris.994@gmail.com</label>
                                    </p>
                                </div> 
                            </div>
                        </div>
                    </Modal>

                </div>
                );
    }
    ;
}
;
