import React, {Component} from'react';
import {render}from'react-dom';
import {Table, Button, Container, Modal, ModalHeader, ModalBody, ModalFooter, formGroup}from 'reactstrap';
import Swal from 'sweetalert';
import axios from 'axios'

export default class AdminTalleres extends Component {
    constructor() {
        super();
        this.state = {
            title: '',
            description: '',
            talleres: [],
            _id: '',
            textButton: 'REGISTRAR',
            modalOpen: false
        };
        this.addTaller = this.addTaller.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);
    }

    componentDidMount() {
        this.getTalleres();
    }

    getTalleres() {
        axios.get('http://localhost:4000/api/talleres').then((response) => {
            console.log(response.data)
            this.setState({talleres: response.data})
        })
    }
    addTaller(e) {
        const datos = {
            title: this.state.title,
            description: this.state.description
        }
        if (this.state._id) {
            e.preventDefault();
            axios.put('http://localhost:4000/api/talleres/' + this.state._id, datos).then((data) => {
                console.log(data);
                Swal({
                    title: 'Tarea Actualizada',
                    text: 'ok',
                    icon: 'success',
                    timer: 2000,
                    button: false
                });
                this.getTalleres();
                this.setState({title: '', description: '', _id: '', textButton: 'REGISTRAR', modalOpen: false});
            }).catch(err => console.error(err));


        } else {
            e.preventDefault();
            axios.post('http://localhost:4000/api/talleres', datos).then((data) => {
                console.log(data);
                Swal({
                    title: 'Tarea Registrada',
                    text: 'ok',
                    icon: 'success',
                    timer: 2000,
                    button: false
                });
                this.getTalleres()
                this.setState({title: '', description: ''})
            })
                    .catch(err => console.error(err));
//        console.log(this.state);

        }
    }
    deleteTaller(id) {
        Swal({
            title: 'Esta seguro de eliminar la tarea',
            text: ' La tarea se eliminara definitivamente',
            icon: 'warning',
            buttons: ['Cancelar', 'Sí'],
            dangerMode: true
        }).then((value) => {
            if (value) {
                axios.delete('http://localhost:4000/api/talleres/'+id) .then((response) => {
                            console.log(response.data);
                            Swal({
                                title: 'Tarea eliminada',
                                icon: 'success',
                                timer: 1000,
                                button: false
                            });
                            this.getTalleres();
                        });
            }
        });


    }

    editTaller(id) {
        axios.get('http://localhost:4000/api/talleres/' + id).then((response) => {
            console.log(response.data);
            this.setState({
                title: response.data.title,
                description: response.data.description,
                _id: response.data._id,
                textButton: 'ACTUALIZAR'
            });
        })

    }
    hideModal() {
        this.setState({title: '', description: '', _id: '', textButton: 'REGISTRAR', modalOpen: false});
    }
    showModal() {
        this.setState({modalOpen: true});
    }

    handleChange(e) {
//        console.log(e.target);
        const{name, value} = e.target;
        this.setState({
            [name]: value
        });
    }

    render() {
        return (
                <div>                   

                    <Container>
                        <br/>
                        <Button color="primary" onClick={this.showModal}>Nueva Tarea</Button>
                        <br/>
                        <br/>
                        <Table>
                            <thead>
                                <tr>
                                    <th>Tarea</th>
                                    <th>Descripcion</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.talleres.map(data => {
                                        return(
                                        <tr key={data._id}>
                                            <td>{data.title}</td>
                                            <td>{data.description}</td>
                                            <td>
                                                <Button color="warning" onClick={() => {
                                                                        this.showModal();
                                                                        this.editTaller(data._id);

                                                                            }}>Edit</Button>{' '}
                                                <Button color="danger" onClick={ () => {
                                                                        this.deleteTaller(data._id)
                                                                            }}>Delete</Button>
                                            </td>
                                        </tr>
                                                )
                                    })
                                }
                            </tbody>
                        </Table>

                        <Modal isOpen={this.state.modalOpen}>
                            <ModalHeader>
                                <div><h3>Insertar Tarea</h3></div>
                            </ModalHeader>

                            <ModalBody>
                                <form onSubmit={this.addTaller} className="container">
                                    <div className="row">
                                        <input name=" title" onChange={this.handleChange} type="text" className="form-control" placeholder="Title" value={this.state.title}/>
                                    </div>
                                    <br/>
                                    <div className="row">
                                        <textarea name="description" onChange={this.handleChange} type="text" className="form-control" placeholder="Description" value={this.state.description}/>
                                    </div>
                                    <div>
                                    <input type="file"/>
                                    </div>
                                    <br/>
                                    <Button id="btnInsertar">{this.state.textButton}</Button>{' '}
                                    <Button id="btnCancelar" onClick={this.hideModal} className="btn btn-danger" data-dismiss="modal" aria-hidden="true">CANCELAR</Button>
                                </form>
                            </ModalBody>               
                        </Modal>
                    </Container>                                                                                             

                </div>
                );
    }

}
;
