import  React, {Component}from'react';
//import {render}from 'react-dom';
import {Table, Button, Container, Modal, ModalHeader, ModalBody, ModalFooter, formGroup, CardTitle, Card, CardBody, CardHeader, CardImg, CardText}from 'reactstrap';
import axios from 'axios';
//import env from "react-dotenv";
const{REACT_APP_HOST} = process.env;

const styles = {
    div: {
        padding: 10,
        textAlign: "left",
    },
    card: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,

        elevation: 5,
    }
}

export default class TallerList extends Component {
    constructor() {
        super();
        this.state = {
            talleres: []
        }
    }

    componentDidMount() {
        this.getTaller();
    }
    getTaller() {
        axios.get(`${REACT_APP_HOST}/api/talleres`).then(response => {
//      axios.get('http://localhost:4000/api/talleres').then(response => {
            console.log(response.data)
            this.setState({talleres: response.data})

        })

    }

    render() {


        return (
                <Container>
                    <div className="row my-5">

                        {
                            this.state.talleres.map((taller) => (
                                        <div className="col-md-3" key={taller._id} style={styles.div}>
                                            <a href="#" style={{color: "black", textDecoration: "none black"}}>
                                                <Card>
                                                <CardHeader>
                                                    <CardTitle className="font-weight-bold" tag="h5">{taller.title}</CardTitle>
                                                </CardHeader>

                                                <CardBody className="text-center">
                                                    <CardImg top width="100%" src={`${REACT_APP_HOST}/public/${taller.image}`} alt="Card image cap" /> 
                                                    <CardText>{taller.description}</CardText>
                                                    <Button color="secondary">Ver Taller</Button>
                                                </CardBody>
                                                </Card>
                                            </a>
                                        </div>
                                        ))
                        }



                    </div>
                </Container>
                )
    }
}
