import  React, {Component}from'react';
import style from '../index.css';
import {Table, Button, Container, Modal, ModalHeader, ModalBody, ModalFooter, formGroup, CardTitle, Card, CardBody, CardHeader, CardImg, CardText}from 'reactstrap';
import {render}from'react-dom';
import axios from 'axios';
import{Link, useParams} from'react-router-dom';

import Navigation from '../components/Navigation';
import Breadcrumb_nav from '../components/Breadcrumb_nav';

import Cookies from 'universal-cookie';



const cookies = new Cookies();
const{REACT_APP_HOST} = process.env;

const styles = {
    div: {
        padding: 10,
        textAlign: "left",

    }

}
var talleresjoin=[];
//var talleresCompletos = [];

class ListTalleres extends Component {
    constructor() {
        super();
        this.state = {
            talleres: [],
            user_talleres: [],
            talleresCompletos: [],
            auxjoin: []
        }

    }

    componentDidMount() {
        if (!cookies.get('nombre')) {
            window.location.href = '/';
        }
        this.getTaller();
    }
    getTaller() {
        const{match} = this.props;
        const id_ar = match.params.id_ar;

        axios.post(`${REACT_APP_HOST}/api/talleres_ar/${id_ar}`).then(response => {
            this.setState({talleres: response.data});
            console.log(response.data);
            this.getUserTaller();
        });

    }

    getUserTaller() {
        const id_user = cookies.get('id');
        axios.get(`${REACT_APP_HOST}/api/user_taller/${id_user}`).then((response) => {
            this.setState({user_talleres: response.data})
            console.log(response.data)
            this.verificarCompletado();
        })

    }

    verificarCompletado() {
        const tc = [];
        this.state.talleres.map((taller, index) => {
            this.state.user_talleres.map((us_taller,i) => {
                if (taller._id === us_taller.id_taller) {
                    tc.push(index);
                    this.setState({talleresCompletos: tc});

                    const returnedTarget = Object.assign(taller, us_taller);
                    talleresjoin[index].push(returnedTarget);
                    this.setState({auxjoin:talleresjoin})
                    console.log(returnedTarget)
                    console.log(talleresjoin)
//                    console.log(this.state.auxjoin);

                }
            })
        })
//        alert(this.state.talleresCompletos[1])
    }

    prueba(index) {
        var estado='';
         this.state.talleresCompletos.map((val,i)=>{
            if(index===val){
//                alert(index+'-'+i+  ' true')
               estado= '1'; 
            }else{
//                alert(index+'-'+i+  ' false')
                estado='0';

            }
//            alert(val)
        })
    return estado;
}
    render() {

        return (
                <div>
                    <Navigation />
                    <Container>

                        {this.state.talleres.length > 0 ?
                                    <div className="row my-5">
                                        {
                                                    this.state.talleres.map((taller, index) => (
                                                                <div className="col-md-3" key={taller._id} style={styles.div}>

                                                                    {this.prueba(index)==='1'?

                                                                    <Link to={`/presentacion/${taller._id}`} style={{color: "black", textDecoration: "none black"}}>
                                                                    <Card className="card_taller">
                                                                    <CardHeader>
                                                                        <CardTitle className="font-weight-bold" tag="h5">{taller.title}</CardTitle>
                                                                        <div className="text-right"><span className="badge bg-success text-white text-right">Completado</span></div>
                                                                    </CardHeader>
                                                                    <CardBody className="text-center">
                                                                        <CardImg className="image" top width="100%" src={`${REACT_APP_HOST}/public_image/${taller.image}`} alt="Card image cap" /> 
                                                                        <CardText>{taller.description}</CardText>

                                                                    </CardBody>
                                                                    </Card>
                                                                    </Link>
                                                                    :

                                                                    <Link to={`/presentacion/${taller._id}`} style={{color: "black", textDecoration: "none black"}}>
                                                                    <Card className="card_taller">
                                                                    <CardHeader>
                                                                        <CardTitle className="font-weight-bold" tag="h5">{taller.title}</CardTitle>
                                                                        <div className="text-right"><span className="badge bg-warning text-white text-right">En curso</span></div>
                                                                    </CardHeader>
                                                                    <CardBody className="text-center">
                                                                        <CardImg className="image" top width="100%" src={`${REACT_APP_HOST}/public_image/${taller.image}`} alt="Card image cap" /> 
                                                                        <CardText>{taller.description}</CardText>

                                                                    </CardBody>
                                                                    </Card>
                                                                    </Link>                                                            
                                                        }
                                                                </div>

                                                                ))


                                        }

                                    </div>

                        : <h5>No hay talleres registrados en esta area</h5>}
                    </Container>

                </div>
                )
    }

}
export default ListTalleres;
