import React, {Component} from 'react';
import{Link} from'react-router-dom';
import {render}from'react-dom';
import {Container, Table}from 'reactstrap';
import Navigation from '../components/Navigation';
import axios from 'axios';

const{REACT_APP_HOST} = process.env;

class AdminResultados extends Component {

    constructor() {
        super();
        this.state = {
            talleres: [],
            usuarios: [],
            usuario_tallers: []
        }


    }
    componentDidMount() {
        this.getTalleres();
    }

    getTalleres() {
        axios.get(`${REACT_APP_HOST}/api/talleres`).then((response) => {
            this.setState({talleres: response.data})
            this.getUsuarios();
        })
    }

    getUsuarios(){
         axios.get(`${REACT_APP_HOST}/api/users`).then((response) => {
            this.setState({usuarios: response.data});
            this.getUsuarioTallers();
        })
    }

    getUsuarioTallers(){
        var user_taller=[];
        var talleres=[];
          this.state.usuarios.map((user,i)=>{

              talleres.push(this.state.talleres);

              var ut=Object.assign(user, talleres);
              user_taller.push(ut);
              this.setState({usuario_tallers:user_taller});
              console.log(this.state.usuario_tallers);
          })
    }

    render() {

        return(
                <div>
                    <Navigation />
                    <div className="containerList my-3"> 
                        <h1>Resultados</h1>

                        <Table>
                            <thead>

                            <tr>

                            <th>Usuario</th>
                            <th>Cedula</th>
                            {
                             this.state.talleres.map((data, index) => (
                                                    <th key={data._id}>Taller {index+1}</th>
                                            ))
                            }

                            </tr>
                            </thead>
                            <tbody>

                            </tbody>
                        </Table>
                    </div>
                </div>

                );
    }
}

export default AdminResultados;
