import React, {Component, useState}from 'react';
import {Container, Row, Col, Button, CardTitle, CardText} from 'reactstrap';
import ReactPlayer from'react-player';

import Styles from '../index.css'


const{REACT_APP_HOST} = process.env;

const styles = {
  border: {
    borderStyle: 'groove',
    color: "#3c3a2d",
    display:'flex'
  }
  }

class Presentacion extends Component {

    constructor() {
        super()
        this.state = {
            value:false
        }
    }
//    const [btnEnable, setBtnEnable]=useState(false);

    evalEnable = (e) => {
                console.log('activo');
        this.setState({value:true});
    }

    render() {
        return(
                <div className="containerFluid mt-3 p-2" >
                    <Row>
                        <Col xs={2} >
                        <div className="pl-2"><CardTitle className="font-weight-bold" tag="h5">Video del curso</CardTitle></div>
                        <div className="pl-2"><CardText>Detalle</CardText></div>
                        <div className="pl-2 text-center"><Button disabled={!this.state.value} color="success">Evaluacion</Button></div>
                        </Col>
                        <Col xs={10}>

                            <ReactPlayer
                                url={`${REACT_APP_HOST}/public_video/video-1617054881699.mp4`}   
                                controls
                                width='100%'
                                height='100%'                       
                                onEnded={this.evalEnable}
                                onPlay={this.evalDisable}
                                />


                        </Col>



                    </Row>
                </div>
                )
    }
}

export default Presentacion;
