import React, {Component} from 'react';
import {Container} from'reactstrap'
import {render}from 'react-dom';
import{Link, useParams} from'react-router-dom';
import axios from'axios';

const{REACT_APP_HOST} = process.env;

class Evaluacion extends Component {

    constructor() {
        super();
        this.state = {
            preguntas_respuestas: []
        }
    }

    componentDidMount() {
        this.getPreguntasRespuestas();

    }

    getPreguntasRespuestas() {
        const {match} = this.props;
        const id_taller = match.params.id;
        axios.get(`${REACT_APP_HOST}/api/respuestas/` + id_taller).then((response) => {
            this.setState({preguntas_respuestas: response.data});
            this.mostarEvaluacion()
        });
    }

    mostarEvaluacion() {
        const contenedor_eval = document.getElementById('contenedor_eval');
        const preguntasYrespuestas = [];
        this.state.preguntas_respuestas.forEach((preguntaAct, numPregunta) => {
            const respuestas = [];
            this.state.preguntas_respuestas.map((data) => {
                respuestas.push(
                        `
                            <label><input type="radio"/>${preguntas_respuestas.pregunta}</label>
                        `
                        );
            });

            preguntasYrespuestas.push(
                    `
                        <div>${preguntaAct.pregunta}</div>
                        <div>${respuestas.join('')}</div>
                    `
                    );

        });
        contenedor_eval.innerHTML = preguntasYrespuestas.join('');

    }

    render() {
        return(
                <Container id='contenedor_eval'>


                </Container>
                );
    }
}

export default Evaluacion;
