import React, {useState, useEffect} from 'react';
import {Container, Row, Col, Button, CardTitle, CardText} from 'reactstrap';
import ReactPlayer from 'react-player';
import{useParams} from 'react-router-dom';
import axios from 'axios';

import Styles from '../index.css'


        const{REACT_APP_HOST} = process.env;

const styles = {
    border: {
        borderStyle: 'groove',
        color: "#3c3a2d",
        display: 'flex'
    }
}

function Presentacion() {

    const {id} = useParams();
    const[taller, getTaller] = useState([]);
    const[value, setstatusBoton] = useState(false);

    useEffect(() => {
        const obtenertaller = () => {
            axios.get(`${REACT_APP_HOST}/api/talleres/${id}`).then(response => {
                getTaller(response);
                console.log(response)
            });
        };      
        obtenertaller();
    }, []);



    const evalEnable = (e) => {
        setstatusBoton(true);
    }
    const evalDisable = (e) => {
        setstatusBoton(false);
    }





    return(
            <div className="containerFluid mt-3 p-2" >
                <Row>
                    <Col xs={2} >
                    <p style={{color: "red", fontSize: 12}}>{id}</p>
                    <div className="pl-2"><CardTitle className="font-weight-bold" tag="h5">Video del curso</CardTitle></div>
                    <div className="pl-2"><CardText>Detalle</CardText></div>
                    <div className="pl-2 text-center"><Button disabled={!value} color="success">Evaluacion</Button></div>
                    </Col>
                    <Col xs={10}>

                    <ReactPlayer
                        url={`${REACT_APP_HOST}/public_video/video-1617054881699.mp4`}   
                        controls
                        width='100%'
                        height='100%'    

                        onEnded={evalEnable}
                        onPlay={evalDisable}
                        />


                    </Col>



                </Row>
            </div>
            )

}

export default Presentacion;
