                                        <Button size="sm" color="danger" onClick={() => {
                                                this.deletePregunta()
                                                    }}><FontAwesomeIcon icon={faTrash}/></Button> 
                                        </Col>                                      
                                    </Row>
                                    </Col>
                                </Row>
