import React, {Component} from 'react';
import{Link} from'react-router-dom';
import {render}from'react-dom';
import {Container, Table}from 'reactstrap';
import Navigation from '../components/Navigation';
import axios from 'axios';

const{REACT_APP_HOST} = process.env;

class AdminResultados extends Component {

    constructor() {
        super();
        this.state = {
            talleres: [],
            usuarios: [],
            usuario_tallers: []
        }


    }
    componentDidMount() {
        this.getTalleres();
    }

    getTalleres() {
        axios.get(`${REACT_APP_HOST}/api/talleres`).then((response) => {
            this.setState({talleres: response.data})
            this.getUsuarios();
        })
    }

    getUsuarios() {
        axios.get(`${REACT_APP_HOST}/api/users`).then((response) => {
            this.setState({usuarios: response.data});
            this.getUserTaller();
        })
    }

    getUserTaller() {
        axios.get(`${REACT_APP_HOST}/api/user_taller/`).then((response) => {
            this.setState({user_talleres: response.data})
            console.log(response.data)
        })

    }

    getUsuarioTallers() {
        var user_taller = [];
        this.state.usuarios.map((user, i) => {

            var ut = Object.assign(user, {taller: this.state.talleres});

            user_taller.push(ut);

            this.setState({usuario_tallers: user_taller});
            console.log(this.state.usuario_tallers);
        })
    }

    render() {

        return(
                <div>
                    <Navigation />
                    <div className="containerList my-3"> 
                        <h1>Resultados</h1>

                        <Table>
                            <thead>

                                <tr>

                                    <th>USUARIO</th>
                                    <th>CEDULA</th>
                                    {
                                        this.state.talleres.map((data, index) => (
                                        <th key={data._id}>{data.title}</th>
                                                    ))
                                    }

                                </tr>
                            </thead>
                            <tbody>

                                    {
                                        this.state.usuarios.map((data, index)=>(
                                           <tr key={data._id}>
                                            <td>{data.nombre}{' '}{data.apellido}</td>
                                            <td>{data.cedula}</td>
                                            {
                                                this.state.talleres.map((data)=>(
                                                        <td key={data._id}>{data.title}</td>

                                                ))
                                            }
                                           </tr>     
                                        ))
                                    }

                            </tbody>
                        </Table>
                    </div>
                </div>

                );
    }
}

export default AdminResultados;
