import React, {Component} from 'react';
import{Link} from'react-router-dom';
import {render}from'react-dom';
import {Container, Table}from 'reactstrap';
import Navigation from '../components/Navigation';
import axios from 'axios';

const{REACT_APP_HOST} = process.env;
var t_c = [];

class AdminResultados extends Component {

    constructor() {
        super();
        this.state = {
            talleres: [],
            usuarios: [],
            user_talleres: []
        }

        this.verificarCompletado = this.verificarCompletado.bind(this);


    }
    componentDidMount() {
        this.getTalleres();
    }

    getTalleres() {
        axios.get(`${REACT_APP_HOST}/api/talleres`).then((response) => {
            this.setState({talleres: response.data})
            this.getUsuarios();
        })
    }

    getUsuarios() {
        axios.get(`${REACT_APP_HOST}/api/users`).then((response) => {
            this.setState({usuarios: response.data});
            this.getUserTaller();
        })
    }

    getUserTaller() {
        axios.get(`${REACT_APP_HOST}/api/user_taller/`).then((response) => {
            this.setState({user_talleres: response.data})
            console.log(response.data)
        })

    }
    getUsuarioTallers() {

        var taller_completado = [];
        this.state.usuarios.map((user, i) => {

            this.state.user_talleres.map((user_taller, j) => {

                if (user._id === user_taller.id_user) {

                    this.state.talleres.map((taller, k) => {
                        if (user_taller.id_taller === taller._id) {
                            t_c.push(k);
                        }
                    })
//                     var returnedobj = Object.assign(taller_completado, {estado: '0'});
                    this.state.talleres.map((taller, j) => {

                        if (this.verificarCompletado(j) === '1') {
                            taller_completado.push('completo');
                        } else {
                            taller_completado.push('incompleto');
                        }


                    })

                }
            })

            console.log(taller_completado)

//            var ut = Object.assign(user, {taller: this.state.talleres});
//
//            user_taller.push(ut);
//
//            this.setState({usuario_tallers: user_taller});
//            console.log(this.state.usuario_tallers);

        })
    }

    verificarCompletado(index) {
        var estado = '0'
        t_c.map((data) => {
            if (data === index) {
                estado = '1';
            }
        })
        return estado;
    }

    prueba(user) {
        var taller_completado = [];
        var returnedobj = [];
        this.state.user_talleres.map((user_taller, j) => {

            if (user === user_taller.id_user) {

                this.state.talleres.map((taller, k) => {
                    if (user_taller.id_taller === taller._id) {
                        t_c.push(k);
                    }
                })

                this.state.talleres.map((taller, j) => {
//                    alert(this.verificarCompletado(j))
                    if (this.verificarCompletado(j) === '1') {
//                        returnedobj = Object.assign(taller, {estado: '1'});
                    } else {
//                        returnedobj = Object.assign(taller, {estado: '0'});
                    }


                })

            }
        })

        console.log(returnedobj)

    }

    render() {

        return(
                <div>
                    <Navigation />
                    <div className="containerList my-3"> 
                        <h1>Resultados</h1>

                        <Table>
                            <thead>

                                <tr>

                                    <th>USUARIO</th>
                                    <th>CEDULA</th>
                                    {
                                        this.state.talleres.map((data, index) => (
                                        <th key={data._id}>{data.title}</th>
                                                    ))
                                    }

                                </tr>
                            </thead>
                            <tbody>

                                {
                                    this.state.usuarios.map((data, index) => (
                                        <tr key={data._id}>
                                            <td>{data.nombre}{' '}{data.apellido}</td>
                                            <td>{data.cedula}</td>

                                            {this.prueba(data._id)}
                                            {
                                                                this.state.talleres.map((data) => (
                                                                        <td key={data._id}>{data.title}</td>

                                                                            ))
                                            }
                                        </tr>
                                                ))
                                }

                            </tbody>
                        </Table>
                    </div>
                </div>

                );
    }
}

export default AdminResultados;
