import React, {Component} from'react';
import {render}from'react-dom';
import {Table, Button, Container, Modal, ModalHeader, ModalBody, ModalFooter, formGroup}from 'reactstrap';
import Swal from 'sweetalert';
import axios from 'axios'

export default class AdminTask extends Component {
    constructor() {
        super();
        this.state = {
            title: '',
            description: '',
            tasks: [],
            _id: '',
            textButton: 'REGISTRAR',
            modalOpen: false
        };
        this.addTask = this.addTask.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);
    }

    componentDidMount() {
        this.getTasks();
    }

    getTasks() {
        fetch('/api/task')
                .then(resp => resp.json())
                .then(data => {
                    console.log(data)
                    this.setState({tasks: data})
                    //console.log(this.state.tasks)
                });

    axios.get().then((response)=>{
        console.log(response.data)
       this.setState({tasks: response.data}) 
    })
    }
    addTask(e) {
        if (this.state._id) {
            e.preventDefault();
            fetch(`/api/task/${this.state._id}`, {
                method: 'PUT',
                body: JSON.stringify(this.state),
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            }).then(resp => resp.json())
                    .then(data => {
                        console.log(data);
                        Swal({
                            title: 'Tarea Actualizada',
                            text: 'ok',
                            icon: 'success',
                            timer: 2000,
                            button: false
                        });
                        this.getTasks();
                        this.setState({title: '', description: '', _id: '', textButton: 'REGISTRAR', modalOpen:false});

                    })
                    .catch(err => console.error(err));
//        console.log(this.state);
        } else {
            e.preventDefault();
            fetch('/api/task', {
                method: 'POST',
                body: JSON.stringify(this.state),
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            }).then(resp => resp.json())
                    .then(data => {
                        console.log(data);
                        Swal({
                            title: 'Tarea Registrada',
                            text: 'ok',
                            icon: 'success',
                            timer: 2000,
                            button: false
                        });
                        this.getTasks()
                        this.setState({title: '', description: ''})
                    })
                    .catch(err => console.error(err));
//        console.log(this.state);
        }



    }
    deleteTask(id) {
        Swal({
            title: 'Esta seguro de eliminar la tarea',
            text: ' La tarea se eliminara definitivamente',
            icon: 'warning',
            buttons: ['Cancelar','Sí'],           
            dangerMode: true
        }).then((value) => {
            if (value) {
                fetch(`/api/task/${id}`, {
                    method: 'DELETE',
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    }
                })
                        .then(resp => resp.json())
                        .then(data => {
                            console.log(data);
                            Swal({
                                title: 'Tarea eliminada',
                                icon: 'success',
                                timer: 1000,
                                button: false
                            });
                            this.getTasks();
                        });
            }
        });


    }

    editTask(id) {
        fetch(`api/task/${id}`, {

        })
                .then(resp => resp.json())
                .then(data => {
                    console.log(data);
                    this.setState({
                        title: data.title,
                        description: data.description,
                        _id: data._id,
                        textButton: 'ACTUALIZAR'
                    });
                });


    }
    hideModal() {
        this.setState({title: '', description: '', _id: '', textButton: 'REGISTRAR', modalOpen: false});
    }
    showModal() {
        this.setState({modalOpen: true});
    }

    handleChange(e) {
//        console.log(e.target);
        const{name, value} = e.target;
        this.setState({
            [name]: value
        });
    }

    render() {
        return (
                <div>                   

                    <Container>
                        <br/>
                        <Button color="primary" onClick={this.showModal}>Nueva Tarea</Button>
                        <br/>
                        <br/>
                        <Table>
                            <thead>
                                <tr>
                                    <th>Tarea</th>
                                    <th>Descripcion</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.tasks.map(data => {
                                        return(
                                        <tr key={data._id}>
                                            <td>{data.title}</td>
                                            <td>{data.description}</td>
                                            <td>
                                                <Button color="warning" onClick={() => {
                                                                        this.showModal();
                                                                        this.editTask(data._id);

                                                                            }}>Edit</Button>{' '}
                                                <Button color="danger" onClick={ () => {
                                                                        this.deleteTask(data._id)
                                                                            }}>Delete</Button>
                                            </td>
                                        </tr>
                                                )
                                    })
                                }
                            </tbody>
                        </Table>

                        <Modal isOpen={this.state.modalOpen}>
                            <ModalHeader>
                                <div><h3>Insertar Tarea</h3></div>
                            </ModalHeader>

                            <ModalBody>
                                <form onSubmit={this.addTask} className="container">
                                    <div className="row">
                                        <input name="title" onChange={this.handleChange} type="text" className="form-control" placeholder="Title" value={this.state.title}/>
                                    </div>
                                    <br/>
                                    <div className="row">
                                        <textarea name="description" onChange={this.handleChange} type="text" className="form-control" placeholder="Description" value={this.state.description}/>
                                    </div>
                                    <br/>
                                    <Button id="btnInsertar">{this.state.textButton}</Button>{' '}
                                    <Button id="btnCancelar" onClick={this.hideModal} className="btn btn-danger" data-dismiss="modal" aria-hidden="true">CANCELAR</Button>
                                </form>
                            </ModalBody>               
                        </Modal>
                    </Container>                                                                                             

                </div>
                );
    }

}
;
