import  React, {Component}from'react';
//import {render}from 'react-dom';
import {Table, Button, Container, Modal, ModalHeader, ModalBody, ModalFooter, formGroup, CardTitle, Card, CardBody, CardHeader, CardImg, CardText}from 'reactstrap';
import axios from 'axios';

export default class TasList extends Component {
    constructor() {
        super();
        this.state = {
            tasks: []
        }
    }

    componentDidMount() {
        this.getTask();
    }
    getTask() {
        axios.get('/api/talleres').then(response => {
            console.log(response.data)
            this.setState({tasks: response.data})

        })

    }

    render() {
        return (
                    <div className="row my-5">

                        {
                            this.state.tasks.map((task) => (
                                        <div className="col-md-3" key={task._id}>
                                            <CardHeader>
                                                <CardTitle className="font-weight-bold" tag="h5">{task.title}</CardTitle>
                                            </CardHeader>
                                            <CardBody className="text-center">
                                                <CardImg top width="100%" src="/assets/318x180.svg" alt="Card image cap" /> 
                                                <CardText>{task.description}</CardText>
                                                <Button color="secondary">Ver Taller</Button>
                                            </CardBody>
                                        </div>
                                        ))
                        }



                    </div>
                )
    }
}
